import React from 'react';
import { Card, CardHeader, CardContent, Typography, Grid } from '@mui/material';
import { makeStyles } from '@mui/styles';
import { AccountCircle, Email, Business, LocationOn, Phone, BusinessCenter, CreditCard, People } from '@mui/icons-material';
import profile from './profile.png'; 

const useStyles = makeStyles((theme) => ({
  card: {
    width: '1000px',
    margin: 'auto',
    marginTop: theme.spacing(20),
    backgroundColor: '#ffffff',
    boxShadow: '0px 20px 30px rgba(255, 215, 0, 0.8)',
    borderRadius: '10px', 
  },
  typography: {
    fontFamily: 'Arial, sans-serif', 
    color: '#333333',
    fontSize: '2.4rem', 
    display: 'flex',
    alignItems: 'center',
  },
  icon: {
    marginRight: theme.spacing(1), 
    color: '#ffd700', 
  },
}));

const UserProfileCard = () => {
  const classes = useStyles();

  return (
    <Card className={classes.card}>
      <CardHeader
        title={<img src={profile} alt="Profile" style={{ width: '20%', height: 'auto' }} />} 
        
        sx={{ textAlign: 'center' }}
      />
      <CardContent>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <Typography variant="body1" className={classes.typography}>
              <AccountCircle className={classes.icon} />
              Name: 
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <Email className={classes.icon} />
              Email: 
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <Business className={classes.icon} />
              Business Type: 
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <LocationOn className={classes.icon} />
              Business Branches: 
            </Typography>
          </Grid>
          <Grid item xs={6}>
            <Typography variant="body1" className={classes.typography}>
              <Phone className={classes.icon} />
              Phone Number: 
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <BusinessCenter className={classes.icon} />
              Company: 
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <CreditCard className={classes.icon} />
              Expected Credit: 
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <People className={classes.icon} />
              Number of Employees: 
            </Typography>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

export default UserProfileCard;
