// src/components/UserProfileCard.js
import React from 'react';
import { Card, CardHeader, CardContent, Typography, Grid } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  card: {
    marginTop: theme.spacing(4), // Add some space below the top
    backgroundColor: '#ffffff', // White background color
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)', // Shadow effect
  },
  typography: {
    fontFamily: 'Arial, sans-serif', // Custom font
    color: '#333333', // Text color
  },
}));

const UserProfileCard = () => {
  const classes = useStyles();

  return (
    <Card className={classes.card}>
      {/* Profile picture as the header */}
      <CardHeader
        title={<img src="profile-picture.jpg" alt="Profile" style={{ width: '100%', height: 'auto' }} />}
        // Removing subheader
        sx={{ textAlign: 'center' }}
      />
      <CardContent>
        <Grid container spacing={2}>
          {/* First column */}
          <Grid item xs={6}>
            <Typography variant="body1" className={classes.typography}>Name: </Typography>
            <Typography variant="body1" className={classes.typography}>Email: </Typography>
            <Typography variant="body1" className={classes.typography}>Business Type: </Typography>
            <Typography variant="body1" className={classes.typography}>Business Branches: </Typography>
          </Grid>
          {/* Second column */}
          <Grid item xs={6}>
            <Typography variant="body1" className={classes.typography}>Phone Number: </Typography>
            <Typography variant="body1" className={classes.typography}>Company: </Typography>
            <Typography variant="body1" className={classes.typography}>Expected Credit: </Typography>
            <Typography variant="body1" className={classes.typography}>Number of Employees: </Typography>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

export default UserProfileCard;
