import React from 'react';
import { Card, CardHeader, CardContent, Typography, Grid } from '@mui/material';
import { makeStyles } from '@mui/styles';
import { AccountCircle, Email, Business, LocationOn, Phone, BusinessCenter, CreditCard, People } from '@mui/icons-material';
import profile from './profile.png'; // Importing the profile image

const useStyles = makeStyles((theme) => ({
  card: {
    width: '1000px',
    margin: 'auto',
    marginTop: theme.spacing(20),
    backgroundColor: '#ffffff',
    boxShadow: '0px 40px 60px rgba(255, 215, 0, 0.8)', // Increased shadow effect
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center', // Centering content horizontally
    padding: theme.spacing(4), // Adding padding to increase space between columns
  },
  profileImage: {
    width: '60%', // Adjusting profile image width
    height: 'auto',
  },
  typography: {
    fontFamily: 'Arial, sans-serif', // Sans-serif font family
    color: '#333333',
    fontSize: '1.6rem', // Increased font size
    display: 'flex',
    alignItems: 'center', // Centering the icon and text vertically
    marginBottom: theme.spacing(2), // Adding margin bottom to increase space between typographies
  },
  icon: {
    marginRight: theme.spacing(1), // Adding space between icon and text
    color: '#ffd700', // Golden color for icons
  },
}));

const UserProfileCard = () => {
  const classes = useStyles();

  return (
    <Card className={classes.card}>
      <CardHeader
        title={<img src={profile} alt="Profile" className={classes.profileImage} />} // Using the imported profile image
        sx={{ textAlign: 'center' }}
      />
      <CardContent>
        <Grid container spacing={6}> {/* Increasing spacing between columns */}
          <Grid item xs={6}>
            <Typography variant="body1" className={classes.typography}>
              <AccountCircle className={classes.icon} />
              Name: John Doe {/* Example name */}
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <Email className={classes.icon} />
              Email: john@example.com {/* Example email */}
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <Business className={classes.icon} />
              Business Type: {/* Example business type */}
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <LocationOn className={classes.icon} />
              Business Branches: {/* Example business branches */}
            </Typography>
          </Grid>
          <Grid item xs={6}>
            <Typography variant="body1" className={classes.typography}>
              <Phone className={classes.icon} />
              Phone Number: {/* Example phone number */}
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <BusinessCenter className={classes.icon} />
              Company: {/* Example company */}
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <CreditCard className={classes.icon} />
              Expected Credit: {/* Example expected credit */}
            </Typography>
            <Typography variant="body1" className={classes.typography}>
              <People className={classes.icon} />
              Number of Employees: 
            </Typography>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

export default UserProfileCard;
